"use strict";

var _rtl = require("../plugins/rtl");

describe('getRtl', function () {
  afterEach(function () {
    document.dir = '';
  });
  test('should return false when document has no dir setting', function () {
    expect((0, _rtl.getRtl)()).toBe(false);
  });
  test('should return false when document is ltr', function () {
    document.dir = 'ltr';
    expect((0, _rtl.getRtl)()).toBe(false);
    document.documentElement.setAttribute('dir', 'ltr');
    expect((0, _rtl.getRtl)()).toBe(false);
  });
  test('should return true when document is rtl', function () {
    document.dir = 'rtl';
    expect((0, _rtl.getRtl)()).toBe(true);
    document.documentElement.setAttribute('dir', 'rtl');
    expect((0, _rtl.getRtl)()).toBe(true);
  });
});