import { responsive } from '../responsive';
describe('responsive', function () {
  test('should transform responsive space values', function () {
    expect(responsive({
      gap: [5, null, 10]
    })).toEqual({
      gap: 5,
      '@media screen and (min-width: 40em)': {},
      '@media screen and (min-width: 52em)': {
        gap: 10
      }
    });
  });
  test('should transform responsive space string values', function () {
    expect(responsive({
      gap: ['5px', null, '10em']
    })).toEqual({
      gap: '5px',
      '@media screen and (min-width: 40em)': {},
      '@media screen and (min-width: 52em)': {
        gap: '10em'
      }
    });
  });
});