"use strict";

var _colors = require("../presets/colors");

describe('color', function () {
  test('should create a color value', function () {
    expect((0, _colors.color)('red').toHexString()).toBe('#ff0000');
  });
  test('should return yellow color values', function () {
    expect(_colors.color.yellow).toBeTruthy();
    expect(_colors.color.yellow100).toBeTruthy();
    expect(_colors.color.yellow300).toBeTruthy();
    expect(_colors.color.yellow500).toBeTruthy();
    expect(_colors.color.yellow700).toBeTruthy();
    expect(_colors.color.yellow900).toBeTruthy();
  });
});